  export interface interface RegisterRegister {
    (property) Register.BytesType: {
    inputs: `0x${string}` | Uint8Array;
    outputs: `0x${string}`;
}BytesType: {
      (property) inputs: `0x${string}` | Uint8Arrayinputs: `0x${string}` | interface Uint8ArrayUint8Array
      (property) outputs: `0x${string}`outputs: `0x${string}`
    }
  }
}

